"use client";

import React from "react";
import { PublicKey } from "@solana/web3.js";
import { WalletIcon } from "lucide-react";

import { formatNumberGrouped, formatNumberShort } from "@/lib/utils";
import { SolAsset } from "@/types/swap";

import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";

import { TokenCombobox } from "@/components/ui/murphy/token-combobox";

type TokenInputProps = {
  assets: SolAsset[];
  disabled: boolean;
  showWalletBalance?: boolean;
  showQuickAmountButtons?: boolean;
  amount?: number;
  onTokenSelect?: (token: SolAsset) => void;
  onAmountChange?: (amount: number) => void;
  onSearch?: ({
    query,
    owner,
  }: {
    query: string;
    owner?: PublicKey;
  }) => Promise<SolAsset[]>;
};

export const TokenInput = React.forwardRef<HTMLInputElement, TokenInputProps>(
  (
    {
      assets,
      disabled = false,
      showWalletBalance = true,
      showQuickAmountButtons = true,
      amount: initAmount = 0,
      onTokenSelect,
      onAmountChange,
      onSearch,
    },
    amountInputRef: React.ForwardedRef<HTMLInputElement>,
  ) => {
    const [amount, setAmount] = React.useState<string>(initAmount.toString());
    const [maxAmount, setMaxAmount] = React.useState<number>(0);
    const [selectedToken, setSelectedToken] = React.useState<SolAsset>();
    const handleInputChange = React.useCallback(
      (newAmount: string) => {
        let formattedAmount: string = "",
          amount: number = 0;
        const newAmountWithoutCommas = newAmount.replace(/,/g, "");
        let decimalPart = newAmountWithoutCommas.split(".")[1];

        if (
          (newAmount.endsWith(",") || newAmount.endsWith(".")) &&
          !newAmount.substring(0, newAmount.length - 1).includes(".")
        ) {
          amount = isNaN(Number.parseFloat(newAmountWithoutCommas))
            ? 0
            : Number.parseFloat(newAmountWithoutCommas);
          formattedAmount = formatNumberGrouped(amount).concat(".");
        } else if (selectedToken) {
          const mintDecimals = selectedToken?.decimals;
          const isDecimalPartInvalid = isNaN(Number.parseFloat(decimalPart));
          if (!isDecimalPartInvalid)
            decimalPart = decimalPart.substring(0, mintDecimals);
          decimalPart = isDecimalPartInvalid
            ? ""
            : ".".concat(
                Number.parseFloat("1".concat(decimalPart))
                  .toString()
                  .substring(1),
              );
          amount = isNaN(Number.parseFloat(newAmountWithoutCommas))
            ? 0
            : Number.parseFloat(newAmountWithoutCommas);
          formattedAmount = formatNumberGrouped(amount)
            .split(".")[0]
            .concat(decimalPart);
        }

        if (amount > maxAmount) {
          setAmount(formatNumberGrouped(maxAmount));
          if (onAmountChange) onAmountChange(maxAmount);
        } else {
          setAmount(formattedAmount);
          if (onAmountChange) onAmountChange(amount);
        }
      },
      [maxAmount, setAmount, selectedToken, onAmountChange],
    );
    React.useEffect(() => {
      setAmount(initAmount.toString());
    }, [initAmount]);
    return (
      <div className="relative w-full space-y-4">
        <div className="flex items-center justify-end gap-2">
          {showWalletBalance && (
            <Button
              variant="ghost"
              size="sm"
              onClick={() => {
                setAmount(formatNumberGrouped(maxAmount));
              }}
            >
              <WalletIcon size={16} />
              {formatNumberShort(maxAmount)}
            </Button>
          )}
          {showQuickAmountButtons && (
            <>
              <Button
                variant="secondary"
                size="sm"
                onClick={() => {
                  setAmount(formatNumberGrouped(maxAmount / 2));
                  onAmountChange && onAmountChange(maxAmount / 2);
                }}
              >
                Half
              </Button>
              <Button
                variant="secondary"
                size="sm"
                onClick={() => {
                  setAmount(formatNumberGrouped(maxAmount));
                  onAmountChange && onAmountChange(maxAmount);
                }}
              >
                Max
              </Button>
            </>
          )}
        </div>
        <div className="flex items-center gap-2">
          <TokenCombobox
            assets={assets}
            onSelect={(token) => {
              setSelectedToken(token);
              setMaxAmount(token?.userTokenAccount?.amount ?? 0);
              setAmount("");
              if (
                amountInputRef &&
                "current" in amountInputRef &&
                amountInputRef.current instanceof HTMLInputElement
              ) {
                amountInputRef.current.focus();
              }
              if (onTokenSelect) onTokenSelect(token);
            }}
            onSearch={onSearch}
          />
          <Input
            ref={amountInputRef}
            type="text"
            placeholder="0"
            className="h-12 text-right"
            inputMode="numeric"
            value={amount ?? undefined}
            disabled={disabled || !selectedToken}
            onChange={(e) => handleInputChange(e.target.value)}
          />
        </div>
      </div>
    );
  },
);

TokenInput.displayName = "TokenInput";

"use client";
import React from "react";
import { ChevronsUpDownIcon } from "lucide-react";
import { Button } from "../button";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import {
  Command,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
} from "@/components/ui/command";
import { SolAsset } from "@/types/swap";
import { PublicKey } from "@solana/web3.js";
import { TokenIcon } from "./token-icon";

type TokenComboboxProps = {
  assets: SolAsset[];
  trigger?: React.ReactNode;
  address?: PublicKey | null;
  showBalances?: boolean;
  onSelect?: (token: SolAsset) => void;
  onSearch?: ({
    query,
    owner,
  }: {
    query: string;
    owner?: PublicKey;
  }) => Promise<SolAsset[]>;
};

export function TokenCombobox({
  assets: initialAssets,
  trigger,
  address,
  showBalances = true,
  onSelect,
  onSearch,
}: TokenComboboxProps) {
  const [open, setOpen] = React.useState(false);
  const [assets, setAssets] = React.useState<SolAsset[]>(initialAssets);
  const [value, setValue] = React.useState("");
  const [searchValue, setSearchValue] = React.useState("");

  const selectedAsset = React.useMemo(
    () => assets.find((asset) => asset.mint.toBase58().toLowerCase() === value),
    [assets, value],
  );
  return (
    <Popover>
      <PopoverTrigger asChild>
        {trigger || (
          <Button
            variant="outline"
            role="combobox"
            size="lg"
            aria-expanded={open}
            className="h-12 w-[300px] justify-start gap-2.5 px-3 font-medium"
          >
            {selectedAsset ? (
              <>
                <TokenIcon asset={selectedAsset} />
                {selectedAsset.symbol}
              </>
            ) : (
              "Select token..."
            )}
            <ChevronsUpDownIcon size={16} className="ml-auto opacity-50" />
          </Button>
        )}
      </PopoverTrigger>
      <PopoverContent className="w-[300px] p-0">
        <Command shouldFilter={false}>
          <CommandInput
            placeholder="Search tokens..."
            onValueChange={setSearchValue}
          />
          <CommandList>
            {assets.length === 0 && (
              <CommandEmpty>
                {searchValue ? "No tokens found." : "Loading..."}
              </CommandEmpty>
            )}
            <CommandGroup>
              {assets.map((asset) => (
                <CommandItem
                  key={asset.mint.toBase58()}
                  value={asset.mint.toBase58().toLowerCase()}
                  onSelect={(currentValue) => {
                    setValue(currentValue === value ? "" : currentValue);
                    setOpen(false);
                    if (onSelect) onSelect(asset);
                  }}
                  className="flex items-center gap-2"
                >
                  <TokenIcon asset={asset} />
                  {asset.symbol}
                </CommandItem>
              ))}
            </CommandGroup>
          </CommandList>
        </Command>
      </PopoverContent>
    </Popover>
  );
}

import { PublicKey } from "@solana/web3.js";
import { clsx, type ClassValue } from "clsx";
import { twMerge } from "tailwind-merge";
import millify from "millify";

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs));
}

export const shortAddress = (address: PublicKey | string) => {
  const key = typeof address === "string" ? address : address.toBase58();
  return `${key.slice(0, 4)}...${key.slice(-4)}`;
};

export const formatUsd = (num: number): string => {
  return formatNumber(num, { style: "currency", currency: "USD" });
};

export const formatNumber = (
  num: number,
  options: Intl.NumberFormatOptions = {},
): string => {
  if (num === null || num === undefined) return "0";

  const absNum = Math.abs(num);
  let decimals = 2;

  if (absNum < 1) {
    decimals = Math.max(2, Math.min(20, Math.ceil(-Math.log10(absNum)) + 2));
  }

  return new Intl.NumberFormat("en-US", {
    minimumFractionDigits: 2,
    maximumFractionDigits: decimals,
    ...options,
  }).format(num);
};

export const formatNumberShort = (num: number): string => {
  if (num < 1000) return formatNumber(num);
  return millify(num, {
    precision: 2,
  });
};

export const formatNumberGrouped = (
  value: number,
  expThreshold: number = 0.0001,
  expPrecision: number = 1,
) => {
  if (value === 0) return "0";

  if (Math.abs(value) < expThreshold) {
    return value.toExponential(expPrecision);
  }

  if (Number.isInteger(value)) {
    return new Intl.NumberFormat("en-US", { useGrouping: true }).format(value);
  }

  const valueParts = value.toString().split(".");
  const decimalPart = valueParts[1] ?? "";
  const leadingZeros = decimalPart.match(/^0*/)?.[0].length ?? 0;
  const minimumFractionDigits = leadingZeros > 0 ? leadingZeros + 1 : 2;

  return new Intl.NumberFormat("en-US", {
    useGrouping: true,
    minimumFractionDigits: minimumFractionDigits,
    maximumFractionDigits: Math.max(2, minimumFractionDigits),
  }).format(value);
};

export const validatePublicKey = (address: PublicKey | string) => {
  try {
    if (typeof address == "string") {
      new PublicKey(address);
    } else {
      address.toBase58();
    }
    return true;
  } catch (error) {
        return false;

  }
};

import { PublicKey, Connection } from "@solana/web3.js";

export interface Config {
    JUPITER_REFERRAL_ACCOUNT?: string;
    JUPITER_FEE_BPS?: number;
}

declare const _default: {
    Config: Config;
};

export type SolAsset = {
  mint: PublicKey;
  name: string;
  symbol: string;
  image: string;
  decimals: number;
  price: number;
  userTokenAccount?: {
    address: PublicKey;
    amount: number;
  };
};

export default _default;
