"use client";

import React from "react";
import Link from "next/link";
import { ExternalLinkIcon } from "lucide-react";

import { shortAddress, formatUsd, formatNumberShort, cn } from "@/lib/utils";
import { SolAsset } from "@/types/assets";

import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Skeleton } from "@/components/ui/skeleton";

import { TokenIcon } from "./token-icon";

type TokenListProps = {
  assets: SolAsset[];
  showBalances?: boolean;
  onClick?: (token: SolAsset) => void;
};

const TokenList = ({
  assets,
  showBalances = true,
  onClick,
}: TokenListProps) => {
  return (
    <Table>
      <TableHeader>
        <TableRow className="hover:bg-transparent">
          <TableHead>Token</TableHead>
          <TableHead>Mint</TableHead>
          <TableHead>Price</TableHead>
          {showBalances && <TableHead>Balance</TableHead>}
          {showBalances && <TableHead>Value</TableHead>}
        </TableRow>
      </TableHeader>
      <TableBody>
        {assets.length === 0 ? (
          <>
            {[...Array(3)].map((_, index) => (
              <TableRow key={index} className="hover:bg-transparent">
                {[...Array(showBalances ? 5 : 3)].map((_, index) => (
                  <TableCell key={index}>
                    {index === 0 ? (
                      <div className="flex items-center gap-3">
                        <Skeleton className="h-[32px] w-[32px] shrink-0 rounded-full" />
                        <Skeleton className="h-[22px] w-full" />
                      </div>
                    ) : (
                      <Skeleton className="h-[22px] w-full" />
                    )}
                  </TableCell>
                ))}
              </TableRow>
            ))}
          </>
        ) : (
          assets.map((asset) => (
            <TableRow
              key={asset.mint.toBase58()}
              className={cn(
                "group odd:bg-muted/25 hover:bg-transparent hover:text-primary hover:odd:bg-muted/25",
                onClick && "cursor-pointer",
              )}
              onClick={() => onClick && onClick(asset)}
            >
              <TableCell>
                <div className="flex items-center gap-2 font-medium">
                  <TokenIcon asset={asset} />
                  {asset.symbol}
                </div>
              </TableCell>
              <TableCell>
                <Link
                  href={`https://solscan.io/token/${asset.mint.toBase58()}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="group inline-flex items-center gap-1"
                  onClick={(e) => e.stopPropagation()}
                >
                  <span className="border-b border-transparent group-hover:border-border">
                    {shortAddress(asset.mint.toBase58())}
                  </span>
                  <ExternalLinkIcon size={16} />
                </Link>
              </TableCell>
              <TableCell>{formatUsd(asset.price || 0)}</TableCell>
              {showBalances && (
                <>
                  <TableCell>
                    {asset.userTokenAccount?.amount &&
                      formatNumberShort(asset.userTokenAccount.amount)}
                  </TableCell>
                  <TableCell>
                    {asset.userTokenAccount?.amount &&
                      formatUsd(
                        asset.userTokenAccount.amount * (asset.price || 0),
                      )}
                  </TableCell>
                </>
              )}
            </TableRow>
          ))
        )}
      </TableBody>
    </Table>
  );
};

export { TokenList };

import { PublicKey, Connection } from "@solana/web3.js";

export interface Config {
    JUPITER_REFERRAL_ACCOUNT?: string;
    JUPITER_FEE_BPS?: number;
}

declare const _default: {
    Config: Config;
};


export default _default;

import { PublicKey } from "@solana/web3.js";
import { clsx, type ClassValue } from "clsx";
import { twMerge } from "tailwind-merge";
import millify from "millify";

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs));
}

export const shortAddress = (address: PublicKey | string) => {
  const key = typeof address === "string" ? address : address.toBase58();
  return `${key.slice(0, 4)}...${key.slice(-4)}`;
};

export const formatUsd = (num: number): string => {
  return formatNumber(num, { style: "currency", currency: "USD" });
};

export const formatNumber = (
  num: number,
  options: Intl.NumberFormatOptions = {},
): string => {
  if (num === null || num === undefined) return "0";

  const absNum = Math.abs(num);
  let decimals = 2;

  if (absNum < 1) {
    decimals = Math.max(2, Math.min(20, Math.ceil(-Math.log10(absNum)) + 2));
  }

  return new Intl.NumberFormat("en-US", {
    minimumFractionDigits: 2,
    maximumFractionDigits: decimals,
    ...options,
  }).format(num);
};

export const formatNumberShort = (num: number): string => {
  if (num < 1000) return formatNumber(num);
  return millify(num, {
    precision: 2,
  });
};

export const formatNumberGrouped = (
  value: number,
  expThreshold: number = 0.0001,
  expPrecision: number = 1,
) => {
  if (value === 0) return "0";

  if (Math.abs(value) < expThreshold) {
    return value.toExponential(expPrecision);
  }

  if (Number.isInteger(value)) {
    return new Intl.NumberFormat("en-US", { useGrouping: true }).format(value);
  }

  const valueParts = value.toString().split(".");
  const decimalPart = valueParts[1] ?? "";
  const leadingZeros = decimalPart.match(/^0*/)?.[0].length ?? 0;
  const minimumFractionDigits = leadingZeros > 0 ? leadingZeros + 1 : 2;

  return new Intl.NumberFormat("en-US", {
    useGrouping: true,
    minimumFractionDigits: minimumFractionDigits,
    maximumFractionDigits: Math.max(2, minimumFractionDigits),
  }).format(value);
};

export const validatePublicKey = (address: PublicKey | string) => {
  try {
    if (typeof address == "string") {
      new PublicKey(address);
    } else {
      address.toBase58();
    }
    return true;
  } catch (error) {
        return false;

  }
};

"use client";

import React from "react";

import { SolAsset } from "@/types/assets";

type IconProps = {
  asset: SolAsset | null;
  size?: number;
};

const TokenIcon = ({ asset, size = 24 }: IconProps) => {
  return (
    <div
      className="relative shrink-0 rounded-full border border-border bg-background p-0"
      style={{
        width: size + 2,
        height: size + 2,
      }}
    >
      <img
        src="/token-icons/placeholder.jpg"
        alt={asset?.symbol ?? asset?.mint.toBase58() ?? ""}
        width={size}
        height={size}
        className="absolute inset-0 rounded-full mt-0"
      />
      <img
        src={asset?.image ?? ""}
        alt={asset?.symbol ?? asset?.mint.toBase58() ?? ""}
        width={size}
        height={size}
        className="absolute inset-0 rounded-full mt-0"
        style={{
          width: size,
          height: size,
        }}
      />
    </div>
  );
};

export { TokenIcon };
