"use client";

import React from "react";
import Link from "next/link";

import {
  VersionedTransactionResponse,
  LAMPORTS_PER_SOL,
} from "@solana/web3.js";

import { useConnection } from "@solana/wallet-adapter-react";
import { formatDistanceToNow } from "date-fns";
import { AlertCircleIcon, ExternalLinkIcon } from "lucide-react";

import { shortAddress, cn } from "@/lib/utils";

import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Skeleton } from "@/components/ui/skeleton";

type TxnListProps = {
  transactions: VersionedTransactionResponse[];
  onClick?: (txn: VersionedTransactionResponse) => void;
};

const TxnList = ({ transactions, onClick }: TxnListProps) => {
  const { connection } = useConnection();
  const [currentSlot, setCurrentSlot] = React.useState<number | null>(null);
  const [averageBlockTime, setAverageBlockTime] = React.useState<number>(0.4);
    React.useEffect(() => {
    console.log("transactions: ",transactions)
},[transactions])
  React.useEffect(() => {
    const init = async () => {
      try {
        const [slot, recentPerformanceSamples] = await Promise.all([
          connection.getSlot(),
          connection.getRecentPerformanceSamples(30),
        ]);
        const totalSampleSeconds = recentPerformanceSamples.reduce(
          (acc, sample) => acc + sample.samplePeriodSecs,
          0,
        );

        const totalSamples = recentPerformanceSamples.reduce(
          (acc, sample) => acc + sample.numSlots,
          0,
        );
        const calculatedAverageBlockTime = totalSampleSeconds / totalSamples;
        setCurrentSlot(slot);
        setAverageBlockTime(calculatedAverageBlockTime);
      } catch (error) {
        console.error("Error fetching block time:", error);
      }
    };
    if (!connection) return;
    init();
  }, [connection]);
  const estimateTimestamp = (blockTime: number | null | undefined) => {
    if (blockTime == null || blockTime == undefined || currentSlot === null) {
      return "Unknown";
    }
    const currentTime = Date.now() / 1000;
    const blockDifference = currentSlot - blockTime;
    const estimatedTimestamp = currentTime - blockDifference * averageBlockTime;
    return formatDistanceToNow(new Date(estimatedTimestamp * 1000), {
      addSuffix: true,
    });
  };
  return (
    <Table>
      <TableHeader>
        <TableRow className="hover:bg-transparent">
          <TableHead>Signature</TableHead>
          <TableHead>Block</TableHead>
          <TableHead>Time</TableHead>
          <TableHead>By</TableHead>
          <TableHead>Fee</TableHead>
        </TableRow>
      </TableHeader>
      <TableBody>
        {transactions?.length === 0 ? (
          <>
            {[...Array(5)].map((_, index) => (
              <TableRow key={index} className="hover:bg-transparent">
                {[...Array(5)].map((_, index) => (
                  <TableCell key={index}>
                    <Skeleton className="h-[22px] w-full" />
                  </TableCell>
                ))}
              </TableRow>
            ))}
          </>
        ) : (
          transactions?.length && transactions.map((txn) => (
            <TableRow
              key={txn.transaction.signatures[0]}
              className={cn(
                "group odd:bg-muted/25 hover:bg-transparent hover:text-primary hover:odd:bg-muted/25",
                onClick && "cursor-pointer",
              )}
              onClick={() => onClick && onClick(txn)}
            >
              <TableCell>
                <Link
                  href={`https://solscan.io/tx/${txn.transaction.signatures[0]}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="group inline-flex items-center gap-1"
                  onClick={(e) => e.stopPropagation()}
                >
                  <ExternalLinkIcon size={16} />
                  <span className="border-b border-transparent group-hover:border-border">
                    {shortAddress(txn.transaction.signatures[0])}
                  </span>
                  {txn.meta?.err && (
                    <AlertCircleIcon className="h-4 w-4 text-destructive" />
                  )}
                </Link>
              </TableCell>
              <TableCell>{txn.blockTime}</TableCell>
              <TableCell>{estimateTimestamp(txn.slot)}</TableCell>
              <TableCell>
                <Link
                  href={`https://solscan.io/account/${txn.transaction.message.staticAccountKeys[0]}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="group inline-flex items-center gap-1"
                  onClick={(e) => e.stopPropagation()}
                >
                  <ExternalLinkIcon size={16} />
                  <span className="border-b border-transparent group-hover:border-border">
                    {shortAddress(txn.transaction.message.staticAccountKeys[0])}
                  </span>
                </Link>
              </TableCell>
              <TableCell>{(txn.meta?.fee || 0) / LAMPORTS_PER_SOL}</TableCell>
            </TableRow>
          ))
        )}
      </TableBody>
    </Table>
  );
};

export default TxnList;

import { PublicKey } from "@solana/web3.js";
import { clsx, type ClassValue } from "clsx";
import { twMerge } from "tailwind-merge";
import millify from "millify";

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs));
}

export const shortAddress = (address: PublicKey | string) => {
  const key = typeof address === "string" ? address : address.toBase58();
  return `${key.slice(0, 4)}...${key.slice(-4)}`;
};

export const formatUsd = (num: number): string => {
  return formatNumber(num, { style: "currency", currency: "USD" });
};

export const formatNumber = (
  num: number,
  options: Intl.NumberFormatOptions = {},
): string => {
  if (num === null || num === undefined) return "0";

  const absNum = Math.abs(num);
  let decimals = 2;

  if (absNum < 1) {
    decimals = Math.max(2, Math.min(20, Math.ceil(-Math.log10(absNum)) + 2));
  }

  return new Intl.NumberFormat("en-US", {
    minimumFractionDigits: 2,
    maximumFractionDigits: decimals,
    ...options,
  }).format(num);
};

export const formatNumberShort = (num: number): string => {
  if (num < 1000) return formatNumber(num);
  return millify(num, {
    precision: 2,
  });
};

export const formatNumberGrouped = (
  value: number,
  expThreshold: number = 0.0001,
  expPrecision: number = 1,
) => {
  if (value === 0) return "0";

  if (Math.abs(value) < expThreshold) {
    return value.toExponential(expPrecision);
  }

  if (Number.isInteger(value)) {
    return new Intl.NumberFormat("en-US", { useGrouping: true }).format(value);
  }

  const valueParts = value.toString().split(".");
  const decimalPart = valueParts[1] ?? "";
  const leadingZeros = decimalPart.match(/^0*/)?.[0].length ?? 0;
  const minimumFractionDigits = leadingZeros > 0 ? leadingZeros + 1 : 2;

  return new Intl.NumberFormat("en-US", {
    useGrouping: true,
    minimumFractionDigits: minimumFractionDigits,
    maximumFractionDigits: Math.max(2, minimumFractionDigits),
  }).format(value);
};

export const validatePublicKey = (address: PublicKey | string) => {
  try {
    if (typeof address == "string") {
      new PublicKey(address);
    } else {
      address.toBase58();
    }
    return true;
  } catch (error) {
        return false;

  }
};
